import React, { useState, useEffect } from 'react';
import { Link, withRouter,Switch, Route } from "react-router-dom";
import { Menu, Layout, Spin } from 'antd';
import {
    MenuUnfoldOutlined,
    MenuFoldOutlined,
    UserOutlined,
    VideoCameraOutlined,
    UploadOutlined,
} from '@ant-design/icons';
import logo from '../../assets/png/logo-white.png';

import './menu.scss';

function MenuSider(props) {

  const [isCollapsed, setIsCollapsed] = useState(false);
  const { routes } = props;
  // console.log(routes); 
    const toggleCollapsed = () => {
        setIsCollapsed(!isCollapsed)
    }

    const { Header, Sider, Content } = Layout;


    return  (
      <Layout>
        <Sider trigger={null} collapsible collapsed={isCollapsed}>
          <div className="logo"><a href='/'><img src={logo} alt='Logo web' /></a></div>
          <Menu theme="dark" mode="inline" defaultSelectedKeys={['1']}>
            <Menu.Item key="/profile" icon={<UserOutlined />}>
              <Link to={'/profile'}>Perfil</Link>
            </Menu.Item>
            <Menu.Item key="/project" icon={<UserOutlined />}>
              <Link to={'/project'}>Proyectos</Link>
            </Menu.Item>
            <Menu.Item key="/profile" icon={<UserOutlined />}>
              <Link to={'/profile'}>Perfil</Link>
            </Menu.Item>
          </Menu>
        </Sider>
        <Layout className="site-layout" >
          <Header className="site-layout-background" style={{ padding: 0 }}>
            {React.createElement(isCollapsed ? MenuUnfoldOutlined : MenuFoldOutlined, {
              className: 'trigger',
              onClick: toggleCollapsed,
            })}
          </Header>
          <Content>
              {routes ? <LoadRoutes routes={routes} /> : <Spin tip='Cargando' style={{width:'200px'}} />}
          </Content>

        </Layout>
      </Layout>
    );
}

function LoadRoutes({ routes }) {

    return (

        <Switch>
            {routes.map((route, index) => (
                <Route 
                    key= {index}
                    path={route.path}
                    exact={route.exact}
                    component={route.component}
                />
            ))}
        </Switch>
    );
}

export default withRouter(MenuSider);
